sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDateToday: function(givenDate){
// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(givenDate);

		var isToday = false;
		// Get the date portion of rightnow and testdate (no timestamp)
		var today = rightnow.getLocalDate();
		var testingDay = testdate.getLocalDate();

		// Compare today and istoday to see if they are the same day
		if(today.compareTo(testingDay) == 0)
			isToday = true;
		return isToday;
	},

isDatePast: function(givenDate){
		var isDateBroken = false;
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(givenDate);

		var today = rightnow.getLocalDate();
		var testingDay = testdate.getLocalDate();

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow) && today.compareTo(testingDay) != 0) {
			isDateBroken = true;		
		}
		return isDateBroken;
	},
    type: 'NeedItUtils'
};
